import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

import { AppProvider } from './context/AppContext';
import Budget from './components/Budget';
import ExpenseTotal from './components/ExpenseTotal';
import ExpenseList from './components/ExpenseList';
import AllocationForm from './components/AllocationForm';
import RemainingBudget from './components/RemainingBudget'; // Corrected component name
import CurrencyDropdown from './components/CurrencyDropdown'; // Corrected component name

const App = () => {
  return (
    <AppProvider>
      <div className='container'>
        <h1 className='mt-3'>Company's Budget Allocation</h1>
        <div className='row mt-3'>
          <div className='col-sm'>
            <Budget />
          </div>
          <div className='col-sm'>
            <RemainingBudget /> {/* Updated component name */}
          </div>
          <div className='col-sm'>
            <ExpenseTotal />
          </div>
          <div className='col-sm'>
            <CurrencyDropdown /> {/* Updated component name */}
          </div>
        </div>
        <h3 className='mt-3'>Allocation</h3>
        <div className='row '>
          <div className='col-sm'>
            <ExpenseList />
          </div>
        </div>
        <h3 className='mt-3'>Change allocation</h3>
        <div className='row mt-3'>
          <div className='col-sm'>
            <AllocationForm />
          </div>
        </div>
      </div>
    </AppProvider>
  );
};

export default App;
