import React, { useContext, useState } from 'react';
import { AppContext } from '../context/AppContext';

const Budget = () => {
    const { budget, currency, expenses, dispatch } = useContext(AppContext);
    const [newBudget, setNewBudget] = useState(budget);

    const handleBudgetChange = (event) => {
        const enteredBudget = parseInt(event.target.value);

        // Ensure the entered budget is not lower than the amount spent so far
        const totalExpenses = expenses.reduce((total, item) => total + item.cost, 0);
        if (enteredBudget < totalExpenses) {
            alert(`Budget cannot be lower than the amount spent so far: ${currency}${totalExpenses}`);
            return;
        }

        // Set the maximum limit to 20,000
        if (enteredBudget > 20000) {
            alert(`Budget cannot exceed 20,000.`);
            return;
        }

        setNewBudget(enteredBudget);
    }

    return (
        <div className='alert alert-secondary'>
            <span>{currency} </span>
            <input
                type="number"
                step="10"
                value={newBudget}
                onChange={handleBudgetChange}
                min={totalExpenses} // Ensure the minimum is the amount spent so far
                max={20000} // Set the maximum limit to 20,000
            ></input>
        </div>
    );
};

export default Budget;
